{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "npm install -g aws-cdk@2",
        "pip install -r requirements.txt",
        "export SONAR_SCANNER_VERSION=4.7.0.2747",
        "export SONAR_SCANNER_HOME=$HOME/.sonar/sonar-scanner-$SONAR_SCANNER_VERSION-linux",
        "curl --create-dirs -sSLo $HOME/.sonar/sonar-scanner.zip https://binaries.sonarsource.com/Distribution/sonar-scanner-cli/sonar-scanner-cli-$SONAR_SCANNER_VERSION-linux.zip",
        "unzip -o $HOME/.sonar/sonar-scanner.zip -d $HOME/.sonar/",
        "export PATH=$SONAR_SCANNER_HOME/bin:$PATH",
        "SONAR_TOKEN=$(aws secretsmanager get-secret-value --secret-id dev/sonar/sonar_token --query SecretString --output text | jq -r .SONAR_TOKEN)",
        "SONAR_URL=$(aws secretsmanager get-secret-value --secret-id dev/sonar/url --query SecretString --output text | jq -r .URL)",
        "echo \"Running sonar-scan\"",
        "sonar-scanner -Dsonar.projectKey=Hca-ocr-sonar -Dsonar.sources=. -Dsonar.host.url=$SONAR_URL -Dsonar.login=$SONAR_TOKEN -Dsonar.qualitygate.wait=true",
        "echo \"Deploying application using AWS CDK\"",
        "cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}

